
public class max_value {

    public static void main(String[] args) {
        System.out.printf(" byte %25d%n", Byte.MAX_VALUE);
        System.out.printf(" short %25d%n", Short.MAX_VALUE);
        System.out.printf(" char %25d%n", (int) Character.MAX_VALUE);
        System.out.printf(" int %25d%n", Integer.MAX_VALUE);
        System.out.printf(" long %25d%n", Long.MAX_VALUE);
        System.out.printf(" float %25f%n", Float.MAX_VALUE);
        System.out.printf(" double %25f%n", Double.MAX_VALUE);
    }
}

